package Oprerator;

public class TypeCastingOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 double x=10;
 System.out.println(x); //implicit type casting or upcasting
 int y;
 double z=20.5;
 y=(int)z;//explicit typecasting or down casting
 System.out.println(y);
	}

}
